import { Button, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'

const Todo = () => {

   const[inputList,setinputList]=useState("");
   const[Items,setItems]=useState([]); 
   const itemEvent =(event)=>{
    setinputList(event.target.value);
   };
   const listOfItems=()=>{
setItems((oldItems) => {
return[...oldItems,inputList];
})

   };
  return (
     <div className='container'>
    <div className='bag'>
      <div>
<Typography variant='h4'>ToDo LIST</Typography>
<br />
<input type="text" id="task" placeholder='Enter a Todo..'  onChange={itemEvent}/>
<br />
<input type="text" id="quan" placeholder='Enter quantity..'  onChange={itemEvent}/>
  
  <Button type='submit' variant='contained' onClick={listOfItems}>Add</Button> 
  <ol id='list'>
   {/* <li>{inputList}</li>*/}
{Items.map((itemval) => {
return <li>{itemval}</li>
})}
  </ol>
  </div>
    </div>
    </div>
  );
}

export default Todo
