import { Button, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'

const Todo = () => {

   const[inputdata,setinputdata]=useState({
todo:"",qun:""
   });
   const[Items,setItems]=useState([]); 
   const itemEvent =(event)=>{
    setinputdata({...inputdata,[event.target.name]:event.target.value})
   };
  }
   const listOfItems=()=>{
setItems([...Items,{todo,qun}])
setinputdata({todo:"",qun:""})


  return (
     <><div className="container">

      <Typography variant='h4'>ToDo LIST</Typography>
      <br />
      <input type="text" name="todo" id="task" value={input.todo} placeholder='Enter a Todo..' onChange={itemEvent} />
      <br /><br />
      <input type="number" name="qun" id="quan" value={input.qun} placeholder='Enter quantity..' onChange={itemEvent} />
      <br /><br />
      <Button type='submit' id="add" variant='contained' onClick={listOfItems}>Add</Button>
    </div><table border={1}>
        <tbody>
          <tr>
            <td>Todo</td>
            <td>Quantity</td>
          </tr>
          {Items.map(() => {
            return (
              <tr>
                <td>{Items.todo} </td>
                <td>{Items.qun}</td>
              </tr>

            );

          })}
        </tbody>
      </table></>
  );
        }



export default Todo;
